
const fs = require('fs');
const path = require('path');

/**
 * 读取帮助文件
 */
class Help{
    constructor() {
        const figurePath = path.resolve(__dirname, 'file/info.txt');
        fs.readFile(figurePath, 'utf8', (err, text) => {
            if(err) {
                console.error(err);
                return;
            }
            console.log(text);
        })
    }
}

module.exports = Help
