#! node

const Help = require('./ReadFile');
const animal = require('./animal')
const Deal = require('./deal')
const Dog = require('./figure/dog');
const Cow = require('./figure/cow');

let speak = 'Everything will be fine';
let who = 'dog';

if (process.argv.length > 4) {
    new Help();
    return;
}

if (process.argv.length === 3) {
    speak = process.argv[2];
}

if (process.argv.length === 4) {
    const argument = process.argv[2].split('-');
    if (argument.length !== 2) {
        new Help();
        return;
    } else {
        who = argument[1]
    }
    speak = process.argv[3];
}

animal.dog()

